#include <stdio.h>

int main() {
    int a, b, c;
    a = b = c = 5;  // 从右到左：c=5, b=5, a=5
    printf("a=%d, b=%d, c=%d\n", a, b, c);  // 输出: a=5, b=5, c=5
    return 0;
}

#include <stdio.h>

int main() {
    int x = 1, y = 2;
    x = y = x + y;  // 先计算 x+y=3，然后 y=3, x=3
    printf("x=%d, y=%d\n", x, y);  // 输出: x=3, y=3
    return 0;
}

#include <stdio.h>

int main() {
    int x = 5, y, z;
    y = z = x++;  // x++返回5，然后x变为6；z=5, y=5
    printf("x=%d, y=%d, z=%d\n", x, y, z);  // 输出: x=6, y=5, z=5
    return 0;
}

