    return 0;
}

#include <stdio.h>

int main() {
    int a = 1, b = 2, c = 3;
    a = b = c = a + b + c;  // 先计算 1+2+3=6，然后 c=6, b=6, a=6
    printf("a=%d, b=%d, c=%d\n", a, b, c);  // 输出: a=6, b=6, c=6
    return 0;
}

