// switch语句示例
int a = 2;
switch(a) {
    case 1: printf("A"); break;
    case 2: printf("B"); // 没有break，会继续执行下一个case
    case 3: printf("C"); break;
    default: printf("D");
}
// 输出：BC

int a = 2;
switch(a) {
    case 1: printf("A"); break;
    case 2: printf("B"); // 没有break，会继续执行下一个case
    case 3: printf("C"); break;
    default: printf("D");
}
// 输出：BC

int x = 5;
switch(x) {
    case 1: printf("一"); break;
    case 2: printf("二"); break;
    default: printf("其他"); // x=5时执行这里
}
// 输出：其他

// 正确的switch语句
switch(2) {
    case 1+1:  // 常量表达式正确
        printf("等于2");
        break;
}
// 输出：等于2

char ch = 'B';
switch(ch) {
    case 'A': case 'a': printf("优秀"); break;
    case 'B': case 'b': printf("良好"); break;
    default: printf("及格");
}
// 输出：良好

// switch只能用于等值判断
int score = 85;
switch(score) {  // 只能判断具体值
    case 90: printf("A"); break;
    case 85: printf("B"); break;
}

// if可以判断范围
if (score >= 90) printf("A");
else if (score >= 80) printf("B");

// 错误的代码
double d = 3.14;
switch(d) {  // 错误：switch表达式不能是浮点型
    case 3.14: printf("π"); break;
}

// 应该用if语句
if (d == 3.14) printf("π");

int score = 85;
switch(score / 10) {  // 85/10=8
    case 10: case 9: printf("A"); break;
    case 8: printf("B"); break;  // 匹配这里
    case 7: printf("C"); break;
    default: printf("D");
}
// 输出：B

// 在switch中嵌套if
int x = 5, y = 10;
switch(x) {
    case 5:
        if (y > 5) printf("x=5且y>5");
        else printf("x=5但y<=5");
        break;
}

// 在if中嵌套switch
if (x > 0) {
    switch(y) {
        case 10: printf("y=10"); break;
    }
}

int x = 3, y = 5;
if(x > 2) {
    switch(y) {
        case 5: printf("Y=5"); break;
        case 6: printf("Y=6"); break;
    }
} else {
    printf("X<=2");
}
// 输出：Y=5

for(int i=1; i<=5; i++) {
    // 表达式1：i=1（初始化）
    // 表达式2：i<=5（条件判断）
    // 表达式3：i++（循环后操作）
    printf("%d", i);
}
// 输出：1 2 3 4 5

for(int i=1; i<=5; i++) {
    printf("%d", i);
}
// 执行过程：
// i=1 → 输出1
// i=2 → 输出2
// i=3 → 输出3
// i=4 → 输出4
// i=5 → 输出5
// i=6 → 退出循环
// 共执行5次

// 正确的累加代码
int sum = 0;
for(int i=1; i<=100; i++) {
    sum += i;
}
printf("1+2+...+100=%d", sum);
// 输出：5050

// 死循环示例
for(int i=1; i<=5; i--) {  // i递减，永远<=5
    printf("%d", i);
    // 输出：1 0 -1 -2 ... 无限循环
}

// 无限循环（条件为空）
for(int i=0; ; i++) {  // 条件为空，默认真
    printf("%d", i);
}

// 三个表达式都可以省略
int i = 1;  // 表达式1移到外面
for(; i<=5; ) {  // 省略表达式1和3
    printf("%d", i);
    i++;  // 在循环体内递增
}

// 甚至三个都省略（无限循环）
for(;;) {  // 三个表达式都省略
    printf("无限循环");
    break;  // 需要break退出
}

// 方法A：遍历判断
int count = 0;
for(int i=1; i<=20; i++) {
    if(i % 2 == 0) count++;
}

// 方法B：直接遍历偶数
count = 0;
for(int i=2; i<=20; i+=2) {
    count++;
}
printf("1~20中有%d个偶数", count);
// 输出：10

for(int i=3; i<=10; i+=2) {
    printf("%d", i);
}
// 执行过程：
// i=3 → 输出3
// i=5 → 输出5
// i=7 → 输出7
// i=9 → 输出9
// i=11 → 退出循环
// 共执行4次

// for循环（已知次数）
for(int i=0; i<5; i++) {
    printf("%d", i);
}

// 等价while循环
int i=0;
while(i < 5) {
    printf("%d", i);
    i++;
}
// 两者输出相同：0 1 2 3 4
