#include <stdio.h>

int main() {
    int a = 10, b = 3;
    printf("%d\n", a / b);  // 输出: 3
    return 0;
}

#include <stdio.h>

int main() {
    int x = 0;
    int result = (1 > 0) || (0 / x);  // 结果为1，不会执行除零操作
    printf("%d\n", result);  // 输出: 1
    return 0;
}

#include <stdio.h>

int main() {
    int i = 5;
    // 警告：未定义行为！
    // int j = i++ + ++i;  // 不要这样写！

    // 正确的写法：
    int j = i++;
    j += ++i;
    printf("i=%d, j=%d\n", i, j);
    return 0;
}

#include <stdio.h>

int main() {
    int x = 2, y = 3;
    x *= y + 5;  // 等价于 x = x * (y + 5)
    printf("%d\n", x);  // 输出: 16
    return 0;
}

#include <stdio.h>

int main() {
    int num = 4;
    if (num % 2 == 0) {
        printf("%d 是偶数\n", num);
    } else {
        printf("%d 是奇数\n", num);
    }
    return 0;
}

#include <stdio.h>

int main() {
    int a = 1, b = 0;
    int result = (a > b) ? a + 1 : b - 1;
    printf("%d\n", result);  // 输出: 2
    return 0;
}

#include <stdio.h>

int main() {
    unsigned int n = 0xAA;
    unsigned int result = n << 4;
    printf("0x%X\n", result);  // 输出: 0xAA0
    return 0;
}

#include <stdio.h>

int main() {
    int i = 5;
    double result = (double)i / 2;
    printf("%.1f\n", result);  // 输出: 2.5
    return 0;
}

#include <stdio.h>

int main() {
    int p = 5;
    int result = p > 0 && p++ > 5;
    printf("p=%d, result=%d\n", p, result);  // 输出: p=6, result=0
    return 0;
}

#include <stdio.h>

int main() {
    int a = 1, b = 2, c = 3;
    int result = !a || b && !c;
    printf("%d\n", result);  // 输出: 0
    return 0;
}
