#include <stdio.h>

int main() {
    printf("%%d\n");  // 输出: %d
    printf("%%f\n");  // 输出: %f
    printf("%%s\n");  // 输出: %s
    return 0;
}

#include <stdio.h>

int main() {
    int num;
    char ch;

    printf("请输入一个整数: ");
    scanf("%d", &num);  // 输入: 123<回车>

    printf("请输入一个字符: ");
    scanf("%c", &ch);   // 这里会读取到换行符，而不是用户输入的字符

    printf("字符的ASCII码: %d\n", ch);  // 输出: 10 (换行符的ASCII码)
    return 0;
}

#include <stdio.h>

int main() {
    int num = 42;
    int width = 8;

    printf("%*d\n", width, num);  // 输出: '      42' (前面有6个空格)
    printf("%*d\n", 5, num);       // 输出: '   42' (前面有3个空格)
    return 0;
}

#include <stdio.h>

int main() {
    double d = 3.14159;
    float f = 2.71828f;

    printf("使用%%f输出double: %f\n", d);    // 输出: 3.141590
    printf("使用%%lf输出double: %lf\n", d);   // 输出: 3.141590
    printf("使用%%f输出float: %f\n", f);     // 输出: 2.718280
    return 0;
}

#include <stdio.h>

int main() {
    int a, b;

    printf("请输入三个整数: ");
    scanf("%d%*d%d", &a, &b);  // 输入: 10 20 30

    printf("a = %d, b = %d\n", a, b);  // 输出: a = 10, b = 30 (跳过了20)
    return 0;
}

#include <stdio.h>

int main() {
    int num = 42;

    printf("右对齐: '%5d'\n", num);   // 输出: '   42'
    printf("左对齐: '%-5d'\n", num);  // 输出: '42   '
    return 0;
}

#include <stdio.h>

int main() {
    char str[10];  // 只能存储9个字符+空字符

    // 危险：如果输入超过9个字符，会导致缓冲区溢出
    scanf("%s", str);

    // 更安全的做法：使用fgets或指定最大宽度
    // scanf("%9s", str);  // 限制最多读取9个字符

    printf("输入: %s\n", str);
    return 0;
}

#include <stdio.h>

int main() {
    int num = 26;

    printf("普通十六进制: %x\n", num);    // 输出: 1a
    printf("带前缀十六进制: %#x\n", num);  // 输出: 0x1a
    printf("大写带前缀: %#X\n", num);     // 输出: 0X1A
    return 0;
}

#include <stdio.h>

int main() {
    char str1[50], str2[50];

    printf("使用%%s读取: ");
    scanf("%s", str1);  // 输入: Hello World
    // str1只包含"Hello"

    // 清空输入缓冲区
    while (getchar() != '\\n');

    printf("使用%%[^\\n]读取: ");
    scanf("%[^\\n]", str2);  // 输入: Hello World
    // str2包含"Hello World"

    printf("str1: %s\n", str1);  // 输出: Hello
    printf("str2: %s\n", str2);  // 输出: Hello World
    return 0;
}

#include <stdio.h>

int main() {
    double pi = 3.14159;

    printf("默认输出: %f\n", pi);      // 输出: 3.141590
    printf("保留2位小数: %.2f\n", pi);  // 输出: 3.14
    printf("保留4位小数: %.4f\n", pi);  // 输出: 3.1416
    return 0;
}
